import sys

class DisjointSet:
    def __init__(self, n):
        self.parent = list(range(n))
        self.size = [1] * n

    def find(self, x):
        if self.parent[x] != x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        root_x, root_y = self.find(x), self.find(y)
        if root_x == root_y:
            return False
        if self.size[root_x] < self.size[root_y]:
            root_x, root_y = root_y, root_x
        self.parent[root_y] = root_x
        self.size[root_x] += self.size[root_y]
        return True


def solve():
    n, m = map(int, input().split())
    dsu = DisjointSet(n + 1)
    g = {}
    for _ in range(m):
        u, v, gcd, lcm = map(int, input().split())
        if not dsu.union(u, v):
            continue
        if gcd * v // math.gcd(gcd, v) not in g:
            g[gcd * v // math.gcd(gcd, v)] = []
        g[gcd * v // math.gcd(gcd, v)].append((gcd, lcm))

    mushrooms = [0] * (n + 1)
    for num in g:
        g[num].sort(key=lambda x: x[1], reverse=True)
        total = num
        for i, (gcd, lcm) in enumerate(g[num]):
            if i == 0 or lcm != g[num][i - 1][1]:
                mushrooms[dsu.find(num)] = total
                break
            total = lcm

    if any(mushroom == 0 for mushroom in mushrooms[1:]):
        print('NO')
        return
    print('YES')
    print(*mushrooms[1:])


