def min_ternary_string(s):
    # Convert string to a list for easier manipulation
    s_list = list(s)

    # First pass: move all '0's to the left
    i = 0
    while i < len(s_list):
        if s_list[i] == '0':
            # Find the next '1' to swap with
            j = i + 1
            while j < len(s_list) and s_list[j] != '1':
                j += 1
            if j < len(s_list):
                s_list[i], s_list[j] = s_list[j], s_list[i]
                i += 1
            else:
                break
        i += 1

    # Second pass: move all '2's to the left
    i = 0
    while i < len(s_list):
        if s_list[i] == '2':
            # Find the previous '1' to swap with
            j = i - 1
            while j >= 0 and s_list[j] != '1':
                j -= 1
            if j >= 0:
                s_list[i], s_list[j] = s_list[j], s_list[i]
                i += 1
            else:
                break
        i += 1

    return ''.join(s_list)

