def segments_intersect(a1, a2, b1, b2):
    def cross(o, a, b):
        return (a[0] - o[0]) * (b[1] - o[1]) - (a[1] - o[1]) * (b[0] - o[0])

    def on_segment(p, q, r):
        if min(p[0], r[0]) <= q[0] <= max(p[0], r[0]) and min(p[1], r[1]) <= q[1] <= max(p[1], r[1]):
            return cross(p, r, q) == 0
        return False

    if on_segment(a1, b1, a2) or on_segment(a1, b2, a2) or on_segment(b1, a1, b2) or on_segment(b2, a1, b2):
        return True

    ab_dx = a2[0] - a1[0]
    ab_dy = a2[1] - a1[1]
    bc_dx = b2[0] - b1[0]
    bc_dy = b2[1] - b1[1]

    if ab_dx * bc_dy == ab_dy * bc_dx:
        min_x1, max_x1 = sorted([a1[0], a2[0]])
        min_x2, max_x2 = sorted([b1[0], b2[0]])
        if max_x1 < min_x2 or max_x2 < min_x1:
            return False

        min_y1, max_y1 = sorted([a1[1], a2[1]])
        min_y2, max_y2 = sorted([b1[1], b2[1]])
        if max_y1 < min_y2 or max_y2 < min_y1:
            return False

        return True
    else:
        o1 = cross(a1, a2, b1)
        o2 = cross(a1, a2, b2)
        o3 = cross(b1, b2, a1)
        o4 = cross(b1, b2, a2)

        if (o1 * o2 < 0) and (o3 * o4 < 0):
            return True
        return False

