# Function to check if a component has an odd cycle
def has_odd_cycle(nodes):
    visited = [False] * (n+1)
    parent = {}
    for node in nodes:
        if not visited[node]:
            stack = [(node, -1)]
            while stack:
                v, p = stack.pop()
                if visited[v]:
                    continue
                visited[v] = True
                parent[v] = p
                for neighbor in edges[v]:
                    if not visited[neighbor]:
                        stack.append((neighbor, v))
            # Now, find if there's an odd cycle using BFS or DFS
            # Using BFS
            queue = []
            for u in nodes:
                if u != parent.get(v, None) and parent.get(u, None) == v:
                    queue.append(u)
            if not queue:
                continue
            visited_cycles = {u: False for u in queue}
            while queue:
                u = queue.pop(0)
                if visited_cycles[u]:
                    continue
                visited_cycles[u] = True
                for v in edges[u]:
                    if visited_cycles.get(v, False):
                        continue
                    if parent.get(v, -1) == parent.get(u, -1):
                        continue
                    if not visited_cycles[v]:
                        queue.append(v)
            for u in visited_cycles:
                if not visited_cycles[u]:
                    continue
                path = []
                current = u
                while current != -1:
                    path.append(current)
                    current = parent.get(current, -1)
                if len(path) % 2 == 1:
                    return True
    return False

