        if not visited[u]:
            queue = deque()
            queue.append(u)
            visited[u] = True
            color[u] = 0
            is_bipartite_u = True
            while queue:
                current = queue.popleft()
                for neighbor in adj[current]:
                    if not visited[neighbor]:
                        visited[neighbor] = True
                        color[neighbor] = 1 - color[current]
                        queue.append(neighbor)
                    else:
                        if color[neighbor] == color[current]:
