def dfs(graph, start, visited, parent):
    """Performs a depth-first search (DFS) on the graph starting from the given node."""
    visited[start] = True
    for neighbor in graph[start]:
        if not visited[neighbor]:
            dfs(graph, neighbor, visited, start)
        elif parent != neighbor:
            return [start, neighbor]


def find_cycles(graph):
    """Finds all cycles of odd length in the graph."""
    visited = [False] * (len(graph) + 1)
    cycles = []
    for i in range(1, len(graph) + 1):
        if not visited[i]:
            cycle = dfs(graph, i, visited, -1)
            if cycle:
                cycles.append(cycle)
    return cycles


def count_ways(graph):
    """Counts the number of ways to form a cycle of odd length."""
    n = len(graph)
    degrees = [len(neighbors) for neighbors in graph]
    odd_degree_nodes = [i for i in range(1, n + 1) if degrees[i] % 2 == 1]
    if len(odd_degree_nodes) == 0:
        return 0
    if len(odd_degree_nodes) == 1:
        return 1
    # We need to add at least one edge to form a cycle.
    ways = 0
    for i in range(len(odd_degree_nodes)):
        for j in range(i + 1, len(odd_degree_nodes)):
            ways += 1
    return ways


