def main():
    import sys
    N, K = map(int, sys.stdin.readline().split())

    if N == 0:
        print(0)
        return

    # Precompute powers of 2 modulo MOD up to K
    pow2 = [1] * (K + 2)
    for i in range(1, K+2):
        pow2[i] = (pow2[i-1] * 2) % MOD

    # Initialize DP
    dp = [[0] * (K + 2) for _ in range(2)]
    # Initially, for t=1, we have either empty set or any set with max m
    for m in range(K + 1):
        if m == 0:
            ways = 1  # only empty set
        else:
            ways = pow2[m - 1]
        dp[0][m] = ways

    # Iterate for each step from 2 to N
    for t in range(2, N + 1):
        new_dp = [[0] * (K + 2) for _ in range(2)]
        for b in [0, 1]:
            for m in range(K + 1):
                if dp[b][m] == 0:
                    continue
                # Determine the minimum new_m based on b
                if b == 1:
                    min_new_m = m + 1
                else:
                    min_new_m = m
                for new_m in range(min_new_m, K + 1):
                    # Decide whether new_b is 1 or 0
                    # new_b can be 1 only if new_m is included in S
                    for new_b in [0, 1]:
                        if new_b == 1:
                            cnt = pow2[new_m - 1]
                        else:
                            cnt = pow2[new_m]
                        new_dp[new_b][new_m] = (new_dp[new_b][new_m] + dp[b][m] * cnt) % MOD
        dp = new_dp

    # Sum all possibilities for the last step
    total = 0
    for b in [0, 1]:
        for m in range(K + 1):
            total = (total + dp[b][m]) % MOD
    print(total)

