    # Create a dictionary to store the indices of matching characters
    matches = {}
    for i in range(n):
        if left[i] != '?' and right[i] != '?':
            if left[i] == right[i]:
                matches[left[i]] = (i + 1, i + 1)
            else:
                matches[left[i]] = (i + 1, 0)
                matches[right[i]] = (0, i + 1)

    # Count the number of pairs that can be formed from the matches
    count = 0
    pairs = []
    for value in matches.values():
        if value[1] != 0:
            count += 1
            pairs.append((value[0], value[1]))

    # Create a list to store the remaining characters
    remaining_left = []
    remaining_right = []
    for i in range(n):
        if left[i] == '?' or right[i] == '?':
            if left[i] != '?':
                remaining_left.append(i + 1)
            elif right[i] != '?':
                remaining_right.append(i + 1)

    # Try to pair up the remaining characters
    i = j = 0
    while i < len(remaining_left) and j < len(remaining_right):
        if left[remaining_left[i] - 1] == '?' or right[remaining_right[j] - 1] == '?':
