    for c in group2:
        prefix2.append(prefix2[-1] + c)

    prefix3 = [0]
    for c in group3:
        prefix3.append(prefix3[-1] + c)

    max_k1 = min(len(prefix1) - 1, m)
    max_k2 = min(len(prefix2) - 1, m // 2)
    max_k3 = min(len(prefix3) - 1, m // 3)

    # Precompute dp_ab[w]: max cost for groups A and B with total weight <=w
    dp_ab = [-float('inf')] * (m + 1)
    dp_ab[0] = 0

    # Process group A (weight 1)
    for w in range(m, 0, -1):
        for k in range(1, len(prefix1)):
            if w >= k:
                if dp_ab[w - k] != -float('inf') and dp_ab[w - k] + prefix1[k] > dp_ab[w]:
                    dp_ab[w] = dp_ab[w - k] + prefix1[k]

    # Process group B (weight 2)
    for w in range(m, 0, -1):
        for l in range(1, len(prefix2)):
            if w >= 2 * l:
                if dp_ab[w - 2 * l] != -float('inf') and dp_ab[w - 2 * l] + prefix2[l] > dp_ab[w]:
                    dp_ab[w] = dp_ab[w - 2 * l] + prefix2[l]

    max_cost = 0
    # Try all possible k3
    for k3 in range(0, len(prefix3)):
        if 3 * k3 > m:
            continue
        rem = m - 3 * k3
        if rem < 0:
            continue
        current_max = prefix3[k3]
        if rem == 0:
            if current_max > max_cost:
                max_cost = current_max
            continue
        # Find the best k1 and k2 such that k1 + 2*k2 <= rem
        for k2 in range(0, len(prefix2)):
            if 2 * k2 > rem:
                break
            rem2 = rem - 2 * k2
            if rem2 < 0:
                continue
            if rem2 == 0:
                total = current_max + prefix2[k2]
                if total > max_cost:
                    max_cost = total
                continue
            best_k1 = min(rem2, len(prefix1)-1)
            possible_cost = prefix2[k2] + prefix1[best_k1]
            if possible_cost > max_cost:
                max_cost = possible_cost
        # Alternatively, find the best k1 and k2 without nested loops
        # Using the dp_ab array
        if rem < 0:
            continue
        best_ab = dp_ab[rem]
        total = prefix3[k3] + best_ab
        if total > max_cost:
            max_cost = total
    print(max_cost)

