import sys
from itertools import product

def main():
    s = sys.stdin.read().strip()
    variables = [c for c in s if c == '?']
    n_vars = len(variables)
    if n_vars == 0:
        print(''.join(['0']*len(s)))
        return

    stack = []
    for c in s:
        if c == '?':
            stack.append(0)
        elif c == '>':
            if not stack:
                print("false")
                return
            x = stack.pop()
            stack.append(x)
        elif c == '<':
            if not stack:
                print("false")
                return
            x = stack.pop()
            stack.append(x)
        elif c == '+':
            if not stack:
                print("false")
                return
            x = stack.pop()
            stack.append(x+1)
        elif c == '-':
            if not stack:
                print("false")
                return
            x = stack.pop()
            stack.append(x-1)
        elif c == '*':
            if not stack:
                print("false")
                return
            x = stack.pop()
            stack.append(x * 10 + 1)
        elif c == '_':
            if not stack:
                print("false")
                return
            x = stack.pop()
            stack.append(x)
        elif c == ':':
            if not stack:
                print("false")
                return
            x = stack.pop()
            stack.append(x)
        elif c == '-':
            if not stack:
                print("false")
                return
            x = stack.pop()
            stack.append(x-1)

    if len(stack) != 0:
        print("false")
        return

    var_indices = {i: idx for i, c in enumerate(s) if c == '?'}
    min_len = len(variables)
    min_val = '0'*min_len
    max_val = '9'*min_len

    best = list(min_val)
    found = False
    for num in range(int(max_val, 10), int(min_val, 10)-1, -1):
        num_str = str(num).zfill(min_len)
        assignment = list(num_str)
        current_stack = stack.copy()
        valid = True
        for i, c in enumerate(s):
            if c == '?':
                var_idx = var_indices[i]
                val = assignment[var_idx]
                current_stack[var_idx] = val
            else:
                if not current_stack:
                    valid = False
                    break
                cmd = c
                if cmd == '>':
                    x = current_stack.pop()
                    current_stack.append(x)
                elif cmd == '<':
                    x = current_stack.pop()
                    current_stack.append(x)
                elif cmd == '+':
                    x = current_stack.pop()
                    current_stack.append(x + 1)
                elif cmd == '-':
                    x = current_stack.pop()
                    current_stack.append(x - 1)
                elif cmd == '*':
                    x = current_stack.pop()
                    current_stack.append(x * 10 + 1)
                elif cmd == '_':
                    x = current_stack.pop()
                    current_stack.append(x)
                elif cmd == ':':
                    x = current_stack.pop()
                    current_stack.append(x)
                elif cmd == '-':
                    x = current_stack.pop()
                    current_stack.append(x - 1)
                else:
                    valid = False
                    break
        if valid and len(current_stack) == 0:
            if ''.join(assignment) < ''.join(best):
                best = assignment
                found = True
    if found:
        print(''.join(best))
    else:
        print("false")

if __name__ == "__main__":
    main()

import sys

def main():
    s = sys.stdin.readline().strip()
    grid = []
    for c in s:
        if c == '\n':
            continue
        grid.append(c)
    rows = len(grid)
    cols = len(grid[0]) if rows > 0 else 0

    # Directions: up, down, left, right, none, loop
    dirs = [(-1,0), (1,0), (0,-1), (0,1), (0,0), (-1,-1)]
    stack = []
    visited = set()

    x, y = 0, 0
    variables = {}
    order = []

    while True:
        if (x, y) in visited:
            break
        visited.add((x, y))
        current_char = grid[y][x]
        if current_char in "0123456789":
            pass
        elif current_char == '>':
            dx, dy = 1, 0
        elif current_char == '<':
            dx, dy = -1, 0
        elif current_char == '^':
            dx, dy = 0, -1
        elif current_char == 'v':
            dx, dy = 0, 1
        elif current_char == '+':
            dx, dy = 0, 0
        elif current_char == '-':
            dx, dy = 0, 0
        elif current_char == '*':
            dx, dy = -1, 1
        elif current_char == '/':
            dx, dy = -1, -1
        elif current_char == '_':
            dx, dy = -1, 0
        elif current_char == '~':
            dx, dy = -1, 1
        elif current_char == '"':
            dx, dy = 0, 0
        else:
            pass

        for _ in range(100):
            x += dx
            y += dy
            if (x, y) in visited:
                break
            if x < 0 or x >= cols or y < 0 or y >= rows:
                break
            next_char = grid[y][x]
            if next_char == ']':
                break
            if next_char in "0123456789":
                pass
            elif next_char == '>':
                nx, ny = x + 1, y
            elif next_char == '<':
                nx, ny = x - 1, y
            elif next_char == '^':
                nx, ny = x, y - 1
            elif next_char == 'v':
                nx, ny = x, y + 1
            elif next_char == '+':
                nx, ny = x, y
            elif next_char == '-':
                nx, ny = x, y
            elif next_char == '*':
                nx, ny = x - 1, y + 1
            elif next_char == '/':
                nx, ny = x - 1, y - 1
            elif next_char == '_':
                nx, ny = x - 1, y
            elif next_char == '~':
                nx, ny = x - 1, y + 1
            elif next_char == '"':
                nx, ny = x, y
            else:
                nx, ny = x, y

            if (nx, ny) in visited:
                break
            if x == nx and y == ny:
                break

        if (x, y) not in visited:
            break

    print('false')

if __name__ == "__main__":
    main()

