import sys
from collections import defaultdict

def main():
    # Read input
    input_str = sys.stdin.read().strip()

    # Initialize variables for simulation
    grid = defaultdict(int)
    x, y = 0, 0
    stack = []
    direction = 'right'  # possible directions: right, left, up, down
    writes = defaultdict(list)  # key: (x, y), value: list of (time, value)
    reads = defaultdict(list)  # key: (x, y), value: list of (time, expected_value)
    time = 0

    # Direction changes
    dir_map = {
        'right': (0, 1),
        'left': (0, -1),
        'up': (-1, 0),
        'down': (1, 0)
    }

    for char in input_str:
        time += 1
        dx, dy = dir_map[direction]

        if char == '>':
            x += dx
        elif char == '<':
            x -= dx
        elif char == '+':
            grid[(x, y)] += 1
            writes[(x, y)].append( (time, grid[(x, y)]) )
        elif char == '-':
            grid[(x, y)] -= 1
            writes[(x, y)].append( (time, grid[(x, y)]) )
        elif char == '*':
            val = grid[(x, y)]
            stack.append(val)
            writes[(x, y)].append( (time, val) )
        elif char == '/':
            if grid[(x, y)] != 0:
                val = grid[(x, y] // 2
                grid[(x, y)] = val
                stack.append(val)
                writes[(x, y)].append( (time, val) )
        elif char == '.':
            if stack:
                val = stack.pop()
                reads[(x, y)].append( (time, val) )
            else:
                val = grid[(x, y)]
                reads[(x, y)].append( (time, val) )
        elif char == '^':
            direction = 'up'
        elif char == 'v':
            direction = 'down'
        elif char == ']':
            if stack:
                prev_dir = stack.pop()
                if prev_dir == 'right':
                    x += dx
                elif prev_dir == 'left':
                    x -= dx
                elif prev_dir == 'up':
                    y += dy
                elif prev_dir == 'down':
                    y -= dy
        elif char == '[':
            stack.append(direction)
            new_dir = None
            if char == '>':
                new_dir = 'right'
            elif char == '<':
                new_dir = 'left'
            elif char == '^':
                new_dir = 'up'
            elif char == 'v':
                new_dir = 'down'
            if new_dir:
                direction = new_dir

    # Check all reads
    possible = True
    for (cx, cy), reads_list in reads.items():
        writes_list = sorted(writes.get((cx, cy), []))
        for (t, expected_val) in reads_list:
            # Find the latest write <= t
            low, high = 0, len(writes_list)-1
            res = -1
            while low <= high:
                mid = (low + high) // 2
                write_time, write_val = writes_list[mid]
                if write_time <= t:
                    res = mid
                    low = mid + 1
                else:
                    high = mid - 1
            if res == -1:
                # No write before or at t, cell is 0
                if expected_val != 0:
                    possible = False
                    break
            else:
                write_time, write_val = writes_list[res]
                if write_time > t:
                    possible = False
                    break
                if write_val != expected_val:
                    possible = False
                    break
        if not possible:
            break

    if not possible:
        print("false")
        return

    # Determine the final values of each cell
    final_values = {}
    for (x, y), writes_list in writes.items():
        if not writes_list:
            final_values[(x, y)] = 0
        else:
            final_values[(x, y)] = writes_list[-1][1]

    # Collect all cells that were written to
    written_cells = list(final_values.keys())
    written_cells.sort(key=lambda cell: (cell[0], cell[1]))

    # Output the values in the order of first write
    output = []
    for cell in written_cells:
        output.append(str(final_values[cell]))
    print(''.join(output))

if __name__ == "__main__":
    main()

def main():
    import sys

    def parse_segments(s):
        segments = []
        current_segment = []
        in_code = True
        for c in s:
            if in_code:
                if c in '><>':
                    segments.append(''.join(current_segment))
                    current_segment = []
                    in_code = False  # Start new segment after direction change
                else:
                    current_segment.append(c)
            else:
                # After a direction change, the next character starts a new segment
                if c != '>':
                    current_segment.append(c)
        segments.append(''.join(current_segment))
        return segments

    def evaluate_segment(segment, stack):
        temp_stack = list(stack)
        for c in segment:
            if c == '>':
                if not temp_stack:
                    return None
                val = temp_stack.pop()
                temp_stack.append(val)
            elif c == '<':
                if not temp_stack:
                    return None
                val = temp_stack.pop()
                temp_stack.append(val)
            elif c == '+':
                if len(temp_stack) < 2:
                    return None
                a = temp_stack.pop()
                b = temp_stack.pop()
                temp_stack.append(a + b)
            elif c == '-':
                if len(temp_stack) < 2:
                    return None
                a = temp_stack.pop()
                b = temp_stack.pop()
                temp_stack.append(b - a)
            elif c == '*':
                if len(temp_stack) < 2:
                    return None
                a = temp_stack.pop()
                b = temp_stack.pop()
