def count_trailing_zeros(m):
    def trailing_zero(n):
        count = 0
        while n > 0:
            n = n // 5
            count += n
        return count

    # Find lower bound
    left, right = 0, 5 * m  # Initial guess for lower bound
    while left < right:
        mid = (left + right) // 2
        if trailing_zero(mid) < m:
            left = mid + 1
        else:
            right = mid
    lower = left

    # Check if lower has exactly m zeros
    if trailing_zero(lower) != m:
        return (0, [])

    # Find upper bound
    left, right = lower, 5 * m  # Initial guess for upper bound
    while left < right:
        mid = (left + right) // 2
        if trailing_zero(mid) <= m:
            left = mid + 1
        else:
            right = mid
    upper = left - 1

    # Now, collect all n from lower to upper inclusive
    count = upper - lower + 1
    return (count, list(range(lower, upper + 1)))

