    y = (a2 * c1 - a1 * c2) / denominator
    return (x, y)

def is_colinear(line1, line2, point):
    a1, b1, c1 = line1
    a2, b2, c2 = line2
    px, py = point
    if a1 * (py - c1) + b1 * (px - a1) == 0 and a2 * (py - c2) + b2 * (px - a2) == 0:
        return True
    return False

