from collections import defaultdict

def prefix_suffix(n, prefixes, suffixes):
    # Create dictionaries to store prefixes and suffixes of each length
    prefix_dict = defaultdict(list)
    suffix_dict = defaultdict(list)

    for prefix in prefixes:
        prefix_dict[len(prefix)].append(prefix)

    for suffix in suffixes:
        suffix_dict[len(suffix)].append(suffix)

    # Initialize the result string
    result = ['P'] * (2 * n - 2)

    # Iterate over all lengths from 1 to n-1
    for length in range(1, n):
        # Get the prefixes and suffixes of the current length
        curr_prefixes = prefix_dict[length]
        curr_suffixes = suffix_dict[length]

        # Find the common prefix between the longest prefix and the longest suffix
        longest_prefix = max(curr_prefixes, key=len)
        longest_suffix = max(curr_suffixes, key=len)
        common_prefix = ''
        for i in range(min(len(longest_prefix), len(longest_suffix))):
            if longest_prefix[i] == longest_suffix[i]:
                common_prefix += longest_prefix[i]
            else:
                break

        # Mark the corresponding strings as prefixes or suffixes
        for i, prefix in enumerate(prefixes):
            if prefix == common_prefix:
                result[i] = 'P'

        for i, suffix in enumerate(suffixes):
            if suffix == common_prefix:
                result[i + n - 1] = 'S'

    return ''.join(result)


