def prefix_suffix(n, prefixes, suffixes):
    # Create a graph where each string is a node
    graph = defaultdict(list)
    for i in range(2 * n - 2):
        if len(prefixes[i]) < n - 1:
            for j in range(2 * n - 2):
                if len(suffixes[j]) < n - 1 and prefixes[i] == suffixes[j][:len(prefixes[i])]:
                    graph[prefixes[i]].append(suffixes[j])
        else:
            for j in range(2 * n - 2):
                if len(prefixes[i]) == len(suffixes[j]) and prefixes[i] == suffixes[j][-len(prefixes[i]):]:
                    graph[suffixes[j]].append(prefixes[i])

    # Perform a depth-first search to find the longest path
    visited = set()
    stack = ['']
    longest_path = ''
    while stack:
        path = stack.pop()
        node = path[-1]
        if node not in visited:
            visited.add(node)
            stack.append(path + node)
            if len(graph[node]) == 0:
                if len(path) > len(longest_path):
                    longest_path = path
            else:
                for neighbor in graph[node]:
                    if neighbor not in visited:
                        stack.append(path + neighbor)

    # Create the output string
    output = ['S'] * (2 * n - 2)
    for i in range(2 * n - 2):
        if prefixes[i] in longest_path:
            output[i] = 'P'

    return ''.join(output)


