import sys

class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.rank = [0] * n
        self.size = [1] * n

    def find(self, x):
        if self.parent[x] != x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        root_x = self.find(x)
        root_y = self.find(y)
        if root_x != root_y:
            if self.rank[root_x] < self.rank[root_y]:
                root_x, root_y = root_y, root_x
            self.parent[root_y] = root_x
            self.size[root_x] += self.size[root_y]
            self.rank[root_x] = max(self.rank[root_x], self.rank[root_y] + 1)

def main():
    input_lines = sys.stdin.readlines()
    for i in range(0, len(input_lines), 2):
        n, k = map(int, input_lines[i].strip().split())
        edges = []
        for j in range(n - 1):
            a, b = map(int, input_lines[i + 1 + j].strip().split())
            edges.append((a - 1, b - 1))
        uf = UnionFind(n)
        for u, v in edges:
            uf.union(u, v)
        sizes = {}
        for i in range(n):
            root = uf.find(i)
            sizes[root] = sizes.get(root, 0) + uf.size[i]
        nodes = sorted(sizes.keys(), key=lambda x: -sizes[x])
        result = []
        for node in nodes[:k]:
            for i in range(n):
                if uf.find(i) == node:
                    result.append(i + 1)
                    uf.parent[i] = i
        print(' '.join(map(str, result)))

