def add(a, b):
    return (a + b) % MOD

def mul(a, b):
    return (a * b) % MOD

def pow(a, b):
    res = 1
    while b:
        if b & 1:
            res = mul(res, a)
        a = mul(a, a)
        b >>= 1
    return res

class BIT:
    def __init__(self, n):
        self.nums = [0] * (n + 1)

    def update(self, i, delta):
        while i < len(self.nums):
            self.nums[i] = add(self.nums[i], delta)
            i += i & -i

    def query(self, i):
        res = 0
        while i:
            res = add(res, self.nums[i])
            i -= i & -i
        return res

def main():
    input_lines = sys.stdin.readlines()
