        dp[0][0] = 1
        for i in range(1, n + 1):
            for j in range(i):
                for c in range(k + 1):
                    dp[i][c] = add(dp[i][c], dp[j][min(c, k)] * bit.query(c))
            for c in range(k + 1):
                bit.update(c, dp[i - 1][c])
