from math import gcd

def lcm(a, b):
    """Compute the lowest common multiple of a and b"""
    return a * b // gcd(a, b)

def solve():
    n = int(input())
    m = len(input())
    k = len(input())

    # Find the GCD of m and k to determine the cycle length
    cycle_length = gcd(m, k)

    # Initialize counters for red spots
    nikephoros_spots = 0
    polycarpus_spots = 0

    # Iterate over the game rounds
    for i in range(1, n + 1):
        # Calculate the index within the cycle for both players
        nikephoros_index = (i - 1) % cycle_length
        polycarpus_index = ((i - 1) % cycle_length + k - m) % cycle_length

        # Determine the winner of the round
        if m > k:
            if input()[nikephoros_index] == 'R' and input()[polycarpus_index] == 'S':
                nikephoros_spots += 1
            elif input()[nikephoros_index] == 'S' and input()[polycarpus_index] == 'P':
                nikephoros_spots += 1
            elif input()[nikephoros_index] == 'P' and input()[polycarpus_index] == 'R':
                nikephoros_spots += 1
        else:
            if input()[nikephoros_index] == 'R' and input()[polycarpus_index] == 'S':
                polycarpus_spots += 1
