import sys
from collections import defaultdict

def main():
    n = int(sys.stdin.readline())
    l = list(sys.stdin.readline().strip())
    r = list(sys.stdin.readline().strip())

    left_counts = defaultdict(int)
    right_counts = defaultdict(int)
    for c in l:
        left_counts[c] += 1
    for c in r:
        right_counts[c] += 1

    # Compute S
    S = 0
    for c in left_counts:
        S += min(left_counts[c], right_counts.get(c, 0))

    # Preprocess for specific pairs
    left_indices = defaultdict(list)
    right_indices = defaultdict(list)
    for i, c in enumerate(l):
        left_indices[c].append(i)
    for j, c in enumerate(r):
        right_indices[c].append(j)

    specific_pairs = []
    for c in left_indices:
        m = min(left_counts[c], right_counts.get(c, 0))
        if m == 0:
            continue
        l_list = left_indices[c][:m]
        r_list = right_indices[c][:m]
        for i in range(m):
            specific_pairs.append((l_list[i], r_list[i]))

    # Collect A and B
    A = []
    B = []
    for i in range(n):
        c_l = l[i]
        if c_l == '?':
            A.append(i)
            continue
        if left_counts[c_l] > right_counts.get(c_l, 0):
            A.append(i)
    for j in range(n):
        c_r = r[j]
        if c_r == '?':
            B.append(j)
            continue
        if right_counts[c_r] > left_counts.get(c_r, 0):
            B.append(j)

    # Determine if additional pairs possible
    has_questionmark_A = any(c == '?' for c in (l[i] for i in A))
    has_questionmark_B = any(c == '?' for c in (r[j] for j in B))
    if has_questionmark_A or has_questionmark_B:
        can_pair = True
    else:
        colors_A = set()
        for i in A:
            colors_A.add(l[i])
        colors_B = set()
        for j in B:
            colors_B.add(r[j])
        if len(colors_A & colors_B) > 0:
            can_pair = True
        else:
            can_pair = False

    if can_pair:
        additional = min(len(A), len(B))
    else:
        additional = 0

    total = S + additional

    # Collect additional pairs
    additional_pairs = []
    used_left = [False] * n
    used_right = [False] * n

    # First, pair ? with ?
    q_left = [i for i in A if l[i] == '?']
    q_right = [j for j in B if r[j] == '?']
    z = min(len(q_left), len(q_right))
    for i in range(z):
        additional_pairs.append((q_left[i], q_right[i]))
        used_left[q_left[i]] = True
        used_right[q_right[i]] = True

    # Now, process non-? in A and B
    leftover_A = [i for i in A if not used_left[i]]
    leftover_B = [j for j in B if not used_right[j]]
    if len(leftover_A) == 0 or len(leftover_B) == 0:
        pass
    else:
        can_form = False
        if len(leftover_A) <= len(leftover_B):
            for i in range(len(leftover_A)):
                c_l = l[leftover_A[i]]
                found = False
                for j in range(len(leftover_B)):
                    c_r = r[leftover_B[j]]
                    if c_l == c_r or c_l == '?' or c_r == '?':
                        additional_pairs.append((leftover_A[i], leftover_B[j]))
                        used_left[leftover_A[i]] = True
                        used_right[leftover_B[j]] = True
                        found = True
                        break
                if found:
                    can_form = True
                    break
        else:
            for i in range(len(leftover_B)):
                c_r = r[leftover_B[i]]
                found = False
                for j in range(len(leftover_A)):
                    c_l = l[leftover_A[j]]
                    if c_l == c_r or c_l == '?' or c_r == '?':
                        additional_pairs.append((leftover_A[j], leftover_B[i]))
                        used_left[leftover_A[j]] = True
                        used_right[leftover_B[i]] = True
                        found = True
                        break
                if found:
                    can_form = True
                    break
        if not can_form:
            pass

    # Output the result
    print(total)
    for pair in specific_pairs:
        a, b = pair
        print(f"{a+1} {b+1}")
    for pair in additional_pairs:
        a, b = pair
        print(f"{a+1} {b+1}")

