def read_input():
    n, x = map(int, input().split())
    vouchers = []
    for _ in range(n):
        li, ri, costi = map(int, input().split())
        vouchers.append((li, ri, costi))
    return n, x, vouchers

def find_min_cost(vouchers, x):
    min_cost = float('inf')
    voucher_dict = {}
    for i, (li, ri, costi) in enumerate(vouchers):
        if x % 2 == 0:
            for j, (lj, rj, costj) in enumerate(vouchers):
                if i != j and not intersect(li, ri, lj, rj):
                    total_duration = (ri - li + 1) + (rj - lj + 1)
                    if total_duration == x:
                        min_cost = min(min_cost, costi + costj)
        else:
            if li + (x // 2) in voucher_dict:
                min_cost = min(min_cost, voucher_dict[li + (x // 2)] + costi)
            if li + (x - (x // 2)) in voucher_dict:
                min_cost = min(min_cost, voucher_dict[li + (x - (x // 2))] + costi)
        voucher_dict[li] = costi
    return min_cost if min_cost != float('inf') else -1

def intersect(li, ri, lj, rj):
    return ri >= lj and rj >= li

def main():
    n, x, vouchers = read_input()
    min_cost = find_min_cost(vouchers, x)
    print(min_cost)

