from functools import lru_cache

def main():
    input_str = sys.stdin.read().strip()
    result = []

    for case in input_str.split('\n'):
        case = case.strip()
        n = len(case)
        if n < 13:
            result.append("false")
            continue

        # We'll represent the path as a list of positions, starting from 0 and moving through the string
        # The path is determined by the structure of the string, but without knowing exactly how, we model each step as requiring a variable
        # For simplicity, assume each character in the string corresponds to a variable, and the path must go through each character
        # So, variables are assigned in the order of the path, which is the order of the string

        # Each variable is assigned a value, and the path must be traversed correctly
        # We need to find the lex smallest assignment of variables 0..k-1 such that the path can be completed

        # Since the exact path rules are unclear, we model each character as requiring the corresponding variable to be 0 to proceed
        # This is a simplification but may work for some cases

        variables = [0] * (n + 1)  # Assuming variables are 0-based up to n-1

        # We'll use memoization to track the current position and variables assigned so far
        @lru_cache(maxsize=None)
        def backtrack(pos, assigned_vars):
            if pos == n:
                return True

            current_char = case[pos]
            var_idx = pos  # Assume each position corresponds to a variable

            for val in range(10):
                if var_idx >= len(assigned_vars):
                    new_vars = assigned_vars.copy()
                    new_vars.append(val)
                    if backtrack(pos + 1, new_vars):
                        return True
                else:
                    if val != assigned_vars[var_idx]:
                        new_vars = assigned_vars.copy()
                        new_vars[var_idx] = val
                        if backtrack(pos + 1, new_vars):
                            return True
            return False

        if backtrack(0, []):
            result.append(''.join(map(str, variables)))
        else:
            result.append("false")

    print('\n'.join(result))

