    n = len(s)
    dp = [float('inf')] * (n + 1)
    dp[0] = 0
    last = 0
    for i in range(n):
        if s[i] == '-':
            last = -1
        elif s[i] == '+':
            last = 1
        else:
            last = 0
        for j in range(i, -1, -1):
            if s[j] == '>' and dp[j] < float('inf') and dp[j+1] < float('inf'):
                dp[j+1] = min(dp[j+1], dp[j] + last)
            elif s[j] == '<' and dp[j] < float('inf') and dp[j-1] < float('inf'):
                dp[j-1] = min(dp[j-1], dp[j] + last)
            elif s[j] == '_' and dp[j] < float('inf') and dp[j+1] < float('inf') and dp[j-1] < float('inf'):
                dp[j-1] = min(dp[j-1], dp[j] + last)
                dp[j+1] = min(dp[j+1], dp[j] + last)
    if dp[-1] == float('inf'):
        return 'false'
    res = ''
    i = n - 1
    while i >= 0:
        if s[i] == '_':
            res = '0' + res
            i -= 1
        elif s[i] == '<':
            res = '0' + res
            i -= 1
        elif s[i] == '>':
            res = '0' + res
            i -= 1
        else:
            res = str(abs(int(res) + last)) + res
            last = 0
            i -= 1
    return res

