import sys
from collections import deque

def main():
    n = int(sys.stdin.readline())
    s = sys.stdin.readline().strip()

    size = 1 << n
    log_table = [0] * (size + 1)
    for i in range(2, size + 1):
        log_table[i] = log_table[i >> 1] + 1

    # Precompute the path for each team
    path = [[] for _ in range(size)]
    for x in range(1, size + 1):
        x0 = x - 1  # convert to 0-based
        current = x0
        for i in range(n):
            current >>= 1
            path[x].append((current, i))

    winning = []

    for x in range(1, size + 1):
        current_x = x
        constraints = {}
        valid = True
        min_x = 1
        max_x = size
        for (parent, i) in path[x]:
            phase = i
            rule = s[phase]
            if rule == '1':
                required = '>'
            else:
                required = '<'

            y = parent
            if y not in constraints:
                constraints[y] = []
            if required == '>':
                if y in constraints:
                    if not constraints[y]:
                        constraints[y] = [x]
                    else:
                        if x <= constraints[y][0]:
                            valid = False
                            break
                        new_min = max(min_x, constraints[y][0] + 1)
                        new_max = min(max_x, size - 1)
                        if new_min > new_max:
                            valid = False
                            break
                        min_x = new_min
                        max_x = new_max
                        constraints[y].append(x)
                else:
                    constraints[y] = [x]
                    min_x = max(min_x, x + 1)
                    max_x = min(max_x, size - 1)
            else:  # required is '<'
                if y in constraints:
                    if not constraints[y]:
                        constraints[y] = [x]
                    else:
                        if x >= constraints[y][0]:
                            valid = False
                            break
                        new_min = max(min_x, 1)
                        new_max = min(max_x, constraints[y][0] - 1)
                        if new_min > new_max:
                            valid = False
                            break
                        min_x = new_min
                        max_x = new_max
                        constraints[y].append(x)
                else:
                    constraints[y] = [x]
                    min_x = max(min_x, 1)
                    max_x = min(max_x, x - 1)

        if valid and min_x <= x <= max_x:
            winning.append(x)

    print(' '.join(map(str, sorted(winning))))

