def get_winning_numbers(n, s):
    count_ones = sum(1 for c in s if c == '1')
    low = 2 ** (n - count_ones)
    high = 2 ** n - 2 ** count_ones + 1

    winning_numbers = []
    for i in range(low, high + 1):
        winning_numbers.append(i)

    return ' '.join(map(str, winning_numbers))


