    sgn_y1 = -1 if y1 < 0 else 1
    sgn_x2 = -1 if x2 < 0 else 1
    sgn_y2 = -1 if y2 < 0 else 1

    cnt = 0
    for a, b, c in points:
        if (sgn_x1 * a + sgn_y1 * b) * (sgn_x2 * a + sgn_y2 * b) > 0:
            cnt += 1

    stdout.write(str(cnt - 1) + '\n')

