import sys
import math

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def solve(x1, y1, x2, y2, roads):
    # Find the greatest common divisor of the two points
    g = gcd(abs(x2 - x1), abs(y2 - y1))
    dx = (x2 - x1) // g
    dy = (y2 - y1) // g

    # Initialize sets to store the lines that intersect with the path
    left_lines = set()
    right_lines = set()
    top_lines = set()
    bottom_lines = set()

    for ax, by, c in roads:
        if ax * dy - by * dx != 0:
            t = c / (ax * dy - by * dx)
            if t >= 0 and (dx > 0 and t < x1 / dx or dx < 0 and t > x1 / dx):
                if ax > 0:
                    left_lines.add((ax, by))
                else:
                    right_lines.add((ax, by))
            elif t <= 0 and (dx > 0 and t > x2 / dx or dx < 0 and t < x2 / dx):
                if ax > 0:
                    right_lines.add((ax, by))
                else:
                    left_lines.add((ax, by))
            elif by > 0:
                if dy > 0 and t < y1 / dy or dy < 0 and t > y1 / dy:
                    top_lines.add((ax, by))
                elif dy > 0 and t > y2 / dy or dy < 0 and t < y2 / dy:
                    bottom_lines.add((ax, by))
            else:
                if dy > 0 and t > y2 / dy or dy < 0 and t < y2 / dy:
                    top_lines.add((ax, by))
                elif dy > 0 and t < y1 / dy or dy < 0 and t > y1 / dy:
                    bottom_lines.add((ax, by))

    # Count the number of lines that intersect with the path
    count = len(left_lines) + len(right_lines) + len(top_lines) + len(bottom_lines)

    return count

