    for _ in range(n-1):
        a, b = map(int, sys.stdin.readline().split())
        edges[a].append(b)
        edges[b].append(a)

    parent = [0] * (n + 1)
    depth = [0] * (n + 1)
    visited = [False] * (n + 1)
    stack = [1]
    visited[1] = True
    order = []
    while stack:
        u = stack.pop()
        order.append(u)
        for v in edges[u]:
            if not visited[v]:
                visited[v] = True
                parent[v] = u
                depth[v] = depth[u] + 1
                stack.append(v)

    size = [0] * (n + 1)
    res = [0] * (n + 1)
    best = [0] * (n + 1)

    def dfs(u, p):
        size[u] = 1
        res[u] = (1 << u)
        for v in edges[u]:
            if v != p:
                dfs(v, u)
                size[u] += size[v]
                res[u] += res[v]
                best[u] = max(best[p], best[v])
        if p != -1:
            best[p] = max(best[p], best[u])

    dfs(1, -1)

    candidates = []
    for u in range(1, n+1):
        if size[u] <= (n - k):
            candidates.append(u)

    candidates.sort(key=lambda x: -res[x])

    selected = set()
    for u in candidates:
        if u in selected:
            continue
        stack = [u]
        visited_sub = {u}
        for v in edges[u]:
            if v not in visited_sub:
                stack.append(v)
                visited_sub.add(v)
        while stack:
            node = stack.pop()
            for neighbor in edges[node]:
                if neighbor not in visited_sub and parent[neighbor] == node:
                    visited_sub.add(neighbor)
                    stack.append(neighbor)
        selected.update(visited_sub)

    result = sorted(selected, reverse=True)
    removed = [x for x in range(1, n+1) if x not in selected]
    print(' '.join(map(str, removed)))

