def main():
    import sys
    n, k, f = map(int, sys.stdin.readline().split())

    # Precompute factorial and inverse factorial up to 2e5
    max_n = 2 * 10**5 + 10
    fact = [1] * (max_n)
    for i in range(1, max_n):
        fact[i] = fact[i-1] * i % MOD
    inv_fact = [1] * (max_n)
    inv_fact[max_n-1] = pow(fact[max_n-1], MOD-2, MOD)
    for i in range(max_n-2, -1, -1):
        inv_fact[i] = inv_fact[i+1] * (i+1) % MOD

    def comb(a, b):
        if a < b or b < 0:
            return 0
        return fact[a] * inv_fact[b] % MOD * inv_fact[a - b] % MOD

    # Number of possible subsets: 2^m, m is the number of bits
    total_bits = n
    sum_total = 0
    dp = [0] * (f + 1)
    dp[0] = 1

    for mask in range(1, 1 << total_bits):
        s = bin(mask).count('1')  # number of set bits
        if s > n:
            continue
        if s == 0:
            continue

        cnt = 0
        for l in range(1, n+1):
            s_bin = bin(mask)[2:].zfill(l)
            if len(s_bin) > l:
                continue
            # Compute the number of strings that have s_bin as a prefix
            # which is 2^(n - len(s_bin))
            add = 2 ** (n - len(s_bin)) if len(s_bin) <= n else 0
            cnt += add
        if cnt > k:
            continue

        if cnt == 0:
            dp[0] = (dp[0] + 1) % MOD
            continue

        c = cnt
        ways = 0
        if c <= k:
            if c >= 0:
                ways = comb(k, c)
        else:
            ways = 0

        if ways == 0:
            continue

        s_new = sum_total + ways
        if s_new > f:
            continue

        if s_new + 1 <= f:
            dp[s_new + 1] = (dp[s_new + 1] + ways) % MOD
        elif s_new == f:
            dp[f] = (dp[f] + ways) % MOD

    print(dp[f] % MOD)

