    n, k, f = map(int, sys.stdin.readline().split())

    # Generate all binary strings of length 1 to n
    strings = []
    for l in range(1, n+1):
        for b in ['0', '1']:
            s = ''.join([b] * l)
            strings.append(s)

    # Sort strings in decreasing order of length
    strings.sort(key=lambda x: -len(x))

    # Compute x_s for each string
    x = {}
    q = deque()
    for s in reversed(strings):
        if len(s) == n:
            x[s] = 0
        else:
            max_x = 0
            for t in strings:
                if s in t and len(t) > len(s):
                    if t in x:
                        max_x = max(max_x, x[t])
            x[s] = max_x
            q.append(s)

    sum_x = sum(x[s] for s in x)
    if sum_x > f:
        print(0)
        return

    rem = f - sum_x
    m = len(x)
    u = [k - xv for s, xv in x.items()]

    dp = [0] * (rem + 1)
    dp[0] = 1
    for i in range(m):
        max_u = u[i]
        new_dp = [0] * (rem + 1)
        for j in range(rem + 1):
            if dp[j] == 0:
                continue
            for d in range(0, max_u + 1):
                if j + d > rem:
                    break
                new_dp[j + d] = (new_dp[j + d] + dp[j]) % MOD
        dp = new_dp

