def min_extra_tiles(hand):
    # Process each hand
    suits = {'m', 'p', 's'}
    counts = {}
    tiles = [t[0] + t[1] for t in hand]

    # Check for existing shuntsu or koutsu
    for t in tiles:
        suit = t[1]
        num = int(t[0])
        counts.setdefault(suit, {})(num) = counts[suit].get(num, 0) + 1

    # Function to check possible shuntsu
    def find_shuntsu():
        for suit in suits:
            sorted_nums = sorted(counts.get(suit, {}))
            n = len(sorted_nums)
            for i in range(n - 2):
                a, b, c = sorted_nums[i], sorted_nums[i+1], sorted_nums[i+2]
                if b == a + 1 and c == b + 1:
                    continue
                else:
                    needed = 3 - n
                    if needed <= 0:
                        return 0
                    else:
                        # Find missing numbers
                        required = set(range(a, a+3))
                        have = set(sorted_nums)
                        missing = required - have
                        return len(missing)
        return float('inf')

    # Function to check possible koutsu
    def find_koutsu():
        min_add = float('inf')
        for suit in suits:
            cnt = counts.get(suit, {})
            for num in cnt:
                current = cnt[num]
                needed = 3 - current
                if needed < 0:
                    continue
                if needed == 0:
                    return 0
                if needed < min_add:
                    min_add = needed
        return min_add if min_add != float('inf') else float('inf')

    # Check all possibilities
    shuntsu_min = find_shuntsu()
    koutsu_min = find_koutsu()
    return min(shuntsu_min, koutsu_min)

