import sys

def min_draws(tiles):
    # Check if already has a mentsu
    if is_mentsu(tiles):
        return 0

    # Generate all possible mentsu options
    suits = ['m', 'p', 's']
    min_draws = float('inf')

    # Try to form a triplet
    for tile in tiles:
        count = tiles.count(tile)
        if count >= 3:
            return 0
        else:
            needed = 3 - count
            if needed < min_draws:
                min_draws = needed

    # Now try all possible sequences for each suit
    for suit in suits:
        nums = []
        for tile in tiles:
            num = int(tile[:-1])
            nums.append(num)
        # Create a sorted list of unique numbers
        unique_nums = sorted(list(set(nums)))
        # For each possible sequence starting at each number
        for i in range(len(unique_nums)):
            start = unique_nums[i]
            for j in range(3):
                if i + j >= len(unique_nums):
                    break
                end = unique_nums[i + j]
                if end - start == j:
                    continue
                else:
                    # How many to add?
                    needed = 3 - (j + 1)
                    if needed <= 0:
                        return 0
                    if needed < min_draws:
                        min_draws = needed

    return min_draws

def is_mentsu(tiles):
    # Check for triplet
    counts = {}
    for tile in tiles:
        t = tile
        suit = t[-1]
        num = int(t[:-1])
        counts[num] = counts.get(num, 0) + 1
        if counts[num] >= 3:
            return True
    # Check for sequence
    suits_present = set()
    for tile in tiles:
        suit = tile[-1]
        suits_present.add(suit)
        num = int(tile[:-1])
        others = []
        for other_tile in tiles:
            if other_tile != tile:
                others.append(int(other_tile[:-1]))
        others_sorted = sorted(others)
        # Find any two consecutive numbers
        found = False
        for x in others_sorted:
            if (num - x == 1) and (x in others_sorted) and (num in others_sorted):
                found = True
                break
        if found:
            return True
    return False

