def compute_sum(s, t, D):
    if s > D:
        return 0
    if t <= D:
        return (t * (t + 1) // 2) - ((s - 1) * s // 2)
    else:
        sum_s_D = (D * (D + 1) // 2) - ((s - 1) * s // 2)
        sum_rest = (t - D) * D
        return sum_s_D + sum_rest

