    dp1 = [[0] * (n + 1) for _ in range(2)]
    dp2 = [[0] * (n + 1) for _ in range(2)]

    # Base cases
    for i in range(n):
        if a[i] == 1:
            dp1[0][i + 1] = dp1[0][i]
            dp2[0][i + 1] = dp2[0][i]
            dp1[1][i + 1] = dp1[1][i] + 1
            dp2[1][i + 1] = dp2[1][i]
        else:
            dp1[0][i + 1] = dp1[0][i]
            dp2[0][i + 1] = dp2[0][i] + 1
            dp1[1][i + 1] = dp1[1][i]
            dp2[1][i + 1] = dp2[1][i] + 1

    # Fill up the rest of the 2D arrays using dynamic programming
    for i in range(2, n + 1):
        for j in range(2):
            for k in range(i - 1):
                if a[i - 1] == 1 and a[k] == 1 and dp1[j][k] + dp2[j][i - 1 - k] > dp1[j][i]:
