def twisty_movement(n, a):
    # Initialize dp arrays to store the maximum length of non-decreasing subsequences
    dp1 = [0] * (n + 1)
    dp2 = [0] * (n + 1)

    # Base case: If there's only one element in the array, the maximum length is 1
    for i in range(1, n + 1):
        dp1[i] = dp2[i] = 1

    # Fill up the dp arrays using dynamic programming
    for i in range(1, n):
        for j in range(i):
            if a[i] == a[j]:
                dp1[i + 1] = max(dp1[i + 1], dp2[j] + 1)
            if a[i] == 2 and a[j] == 1:
                dp1[i + 1] = max(dp1[i + 1], dp2[j] + 1)
            if a[i] == 1 and a[j] == 2:
                dp2[i + 1] = max(dp2[i + 1], dp1[j] + 1)

    # Return the maximum value in the dp arrays
    return max(max(dp1), max(dp2))


