import sys
from functools import lru_cache

def main():
    n = int(sys.stdin.readline())
    A = sys.stdin.readline().strip()
    digits = [int(c) for c in A]

    @lru_cache(maxsize=None)
    def dp(pos, h, m):
        if pos == len(digits):
            return (0, '')
        current_player = 'H' if (h + m) % 2 == 0 else 'M'
        if current_player == 'H':
            if h >= n:
                return (-float('inf'), '')
            new_h = h + 1
            new_m = m
            exp = n - new_h
            contribution = digits[pos] * (10 ** exp)
            next_sum, next_path = dp(pos + 1, new_h, new_m)
            total_sum = next_sum + contribution
            return (total_sum, 'H' + next_path)
        else:
            if m >= n:
                return (-float('inf'), '')
            new_m = m + 1
            new_h = h
            exp = n - new_m
            contribution = digits[pos] * (10 ** exp)
            next_sum, next_path = dp(pos + 1, new_h, new_m)
            total_sum = next_sum + contribution
            return (total_sum, 'M' + next_path)

    max_sum, path = dp(0, 0, 0)
    print(path)

