def get_max_prize(n, A):
    max_prize = [[0 for _ in range(n + 1)] for _ in range(2)]
    sequence = [[['' for _ in range(n + 1)] for _ in range(2)] for _ in range(2 ** (n + 1))]

    for i in range(2 ** (n + 1)):
        for j in range(n + 1):
            max_prize[0][j] = -1
            max_prize[1][j] = -1

    for i in range(2 ** n, 0, -1):
        for j in range(n):
            if i + (1 << j) <= 2 ** n:
                max_val = max(max_prize[0][j], max_prize[1][j])
                if A[i + (1 << j) - 1] > A[i - 1]:
                    max_prize[0][j + 1] = max(max_prize[0][j + 1], max_val + int(A[i - 1]))
                    sequence[i + (1 << j) - 1][0][j + 1] = 'H'
                if A[i + (1 << j) - 1] > A[i + (1 << j) - 1 - (1 << j)]:
                    max_prize[1][j + 1] = max(max_prize[1][j + 1], max_val + int(A[i + (1 << j) - 1 - (1 << j)]))
                    sequence[i + (1 << j) - 1][1][j + 1] = 'M'

    result = ''
    for i in range(2 ** n, 0, -1):
        if A[i - 1] > A[i - 1 - (1 << (n - 1))]:
            result += 'M'
            n -= 1
        else:
            result += 'H'
            n -= 1

    return result[::-1]


