from itertools import chain, combinations

def powerset(iterable):
    """Generate all possible subsets of an iterable."""
    s = list(iterable)
    return chain.from_iterable(combinations(s, r) for r in range(len(s)+1))

def solve():
    """Solve the Gluttony problem."""
    n = int(input())
    a = [int(x) for x in input().split()]

    # Initialize result array with -1
    b = [-1] * n

    # Generate all possible subsets of indices
    subsets = list(powerset(range(n)))

    # Iterate over all possible subsets
    for subset in subsets:
        if len(subset) != 0 and len(subset) != n:
            # Calculate sum of elements at positions in subset
            subset_sum = sum(a[i] for i in subset)

            # Try to place first element of subset in b
            for i in subset:
                if b[i] == -1:
                    b[i] = a[subset.index(max(subset, key=lambda x: a[x]))]
                    break

            # Place remaining elements of subset in b
            for i in subset:
                if i != subset.index(max(subset, key=lambda x: a[x])):
                    b[i] = subset_sum - b[subset.index(max(subset, key=lambda x: a[x]))]

            # Check if sums of elements at positions in a and b are different for all subsets
            for subset2 in subsets:
                if len(subset2) != 0 and len(subset2) != n:
                    if set(subset).issubset(set(subset2)) and sum(a[i] for i in subset2) == sum(b[i] for i in subset2):
                        b = [-1] * n
                        break

            # If we found a valid permutation, print it and break
            if all(x != -1 for x in b):
                print(' '.join(map(str, b)))
                return

