def solve(n, a):
    if n == 1:
        return a
    elif n == 2:
        return ' '.join(map(str, sorted(a, reverse=True)))
    else:
        # Calculate the maximum sum of subsets of size n-1
        max_sum = 0
        for mask in range(1, 1 << (n - 1)):
            subset_sum = sum(a[i] for i in range(n - 1) if (mask & (1 << i)) > 0)
            max_sum = max(max_sum, subset_sum)

        # Check if it's possible to construct the array b
        if max_sum >= a[-1]:
            return '-1'

        # Construct the array b
        b = [0] * n
        for i in range(n - 1):
            mask = (1 << (n - 1)) - 1 - (1 << i)
            subset_sum = sum(a[j] for j in range(n - 1) if (mask & (1 << j)) > 0)
            b[i] = a[-1] - subset_sum

        # Place the largest element at the end
        b[-1] = a[-1]

        return ' '.join(map(str, b))

