MOD = 10**9 + 7

def main():
    import sys
    n, mod = map(int, sys.stdin.readline().split())

    # Precompute factorials modulo MOD
    fact = [1] * (n+1)
    for i in range(1, n+1):
        fact[i] = fact[i-1] * i % mod

    # Precompute inverse factorials modulo MOD
    inv_fact = [1] * (n+1)
    inv_fact[n] = pow(fact[n], mod-2, mod)
    for i in range(n-1, -1, -1):
        inv_fact[i] = inv_fact[i+1] * (i+1) % mod

    # Now, the main DP
    dp = [[[[0]*(2*n+1) for _ in range(2*n+1)] for __ in range(1<<n)] for ___ in range(1<<n)]

    # Initialize
    dp[0][0][0][0][0] = 1

    for k in range(n):
        for s_p in range(1 << n):
            for s_q in range(1 << n):
                for delta_inv in range(2*n+1):
                    if dp[k][s_p][s_q][delta_inv] == 0:
                        continue
                    # Get the available elements for next step
                    used_p = bin(s_p).count('1')
                    used_q = bin(s_q).count('1')
                    remaining = n - k - 1
                    if used_p != used_q:
                        continue
                    if k == n:
                        continue
                    # Generate all possible next elements for p and q
                    available = []
                    for i in range(n):
                        if not (s_p & (1 << i)) and not (s_q & (1 << i)):
                            available.append(i)
                    m = len(available)
                    for i in range(m):
                        x = available[i]
                        for j in range(i+1, m):
                            y = available[j]
                            if x >= y:
                                continue
                            new_s_p = s_p | (1 << x)
                            new_s_q = s_q | (1 << y)
                            # Compute the number of ways to choose the rest
                            ways = fact[m] * fact[m] % mod
                            if k + 1 < n:
                                ways = ways * inv_fact[m] % mod
                            # Update the inversion counts
                            # Calculate the additional inversions caused by x and y
                            # For p: adding x contributes inv_p += cnt_p
                            # For q: adding y contributes inv_q += cnt_q
                            # cnt_p is the number of elements already in p that are > x
                            cnt_p = bin(s_p).count('1') - bin((s_p & ~((1 << x) - 1)) >> 1).bit_count()
                            cnt_q = bin(s_q).count('1') - bin((s_q & ~((1 << y) - 1)) >> 1).bit_count()
                            new_delta_inv = delta_inv + cnt_p - cnt_q
                            if new_delta_inv > n:
                                new_delta_inv = n
                            elif new_delta_inv < -n:
                                new_delta_inv = -n
                            dp[k+1][new_s_p][new_s_q][new_delta_inv] = (
                                (dp[k+1][new_s_p][new_s_q][new_delta_inv] + 
                                 ways * dp[k][s_p][s_q][delta_inv]) % mod
                            )

    # Sum over all possible delta_inv > 0
    total = 0
    for s_p in range(1 << n):
        for s_q in range(1 << n):
            for delta_inv in range(1, 2*n+1):
                total = (total + dp[n-1][s_p][s_q][delta_inv]) % mod
    print(total % mod)

if __name__ == '__main__':
    main()

