def count_inversions(n, mod):
    @lru_cache(maxsize=None)
    def dp(i, j, k):
        if i == j:
            return 0
        ans = 0
        for x in range(i, j):
            ans += (k + 1) * dp(i, x, k + 1) % mod * dp(x + 1, j, k + 1) % mod
            ans %= mod
        return ans

    ans = 0
    for inversions in range((n * (n - 1)) // 2 + 1):
        ans += dp(1, n, 0) * ((-1) ** (inversions >> 1)) * comb(n - 1, inversions) % mod
        ans %= mod
    return ans


def comb(n, r):
    res = 1
    for i in range(r):
        res *= n - i
        res %= 10**9 + 7
        res *= pow(i + 1, -1, 10**9 + 7)
        res %= 10**9 + 7
    return res


