def count_inversions(n, mod):
    @lru_cache(maxsize=None)
    def count_inversions_helper(i, j):
        if i >= j:
            return 0
        min_val = min(arr[i:j+1])
        min_idx = arr[i:j+1].index(min_val) + i
        left_inversions = sum(count_inversions_helper(i, k-1) for k in range(min_idx, i, -1))
        right_inversions = sum(count_inversions_helper(k+1, j) for k in range(min_idx, j+1))
        return left_inversions + right_inversions + (j-i+1) * (min_idx-i)

    arr = list(range(1, n+1))
    total_inversions = sum(count_inversions_helper(i, n-1) for i in range(n))

    dp = [[0] * (n+1) for _ in range(n+1)]
    for i in range(1, n+1):
        for j in range(1, i+1):
            if i == j:
                dp[i][j] = 1
            else:
                dp[i][j] = (sum(dp[k][i-j+1] for k in range(i)) + (i-j) * (n-i)) % mod

    ans = 0
    for i in range(1, n+1):
        for j in range(i+1, n+1):
            inversions = count_inversions_helper(i, j-1)
            if inversions > total_inversions - inversions and i < j:
                ans += dp[j][i]
    return ans % mod

