class BIT:
    def __init__(self, n):
        self.n = n
        self.tree = [0] * (n + 1)

    def update(self, i, val):
        while i <= self.n:
            self.tree[i] += val
            i += i & -i

    def query(self, i):
        res = 0
        while i:
            res += self.tree[i]
            i -= i & -i
        return res

    def range_query(self, i, j):
        return self.query(j) - self.query(i - 1)

def solve(N, H, M, restrictions):
    maxes = [0] * (N + 1)
    for l, r, x in restrictions:
        maxes[l] = max(maxes[l], x)
        maxes[r+1] = max(maxes[r+1], x)

    for i in range(1, N+1):
        maxes[i] = max(maxes[i-1], maxes[i])

    dp = [[0] * (H + 1) for _ in range(N + 1)]
    for i in range(1, N+1):
        for h in range(H, -1, -1):
            if h > maxes[i]:
                dp[i][h] = dp[i-1][h] + h**2
            else:
                dp[i][h] = max(dp[i-1][h], dp[i-1][h+1] + h**2)

    return dp[N][-1]

