def find_min_k(a, b):
    if a == b:
        return 0
    d = abs(a - b)
    min_lcm = float('inf')
    best_k = 0
    # Consider all divisors of d
    for g in get_divisors(d):
        if g == 0:
            continue
        t = d // g
        # Find m and n such that m = n + t, gcd(m, n) = 1, and m*n is minimized
        # Since m = n + t, and m > n >= 1
        n_min = 1
        m_min = n_min + t
        # Check if m_min and n_min are coprime
        if math.gcd(m_min, n_min) != 1:
            # Find the smallest n where m and n are coprime
            n = 1
            while True:
                m = n + t
                if math.gcd(m, n) == 1:
                    break
                n += 1
            product = g * m * n
        else:
            product = g * m_min * n_min
        # Now compute the corresponding k
        x = g * m
        k = x - a
        if k < 0:
            continue
        current_lcm = product
        if current_lcm < min_lcm or (current_lcm == min_lcm and k < best_k):
            min_lcm = current_lcm
            best_k = k
    return best_k

