def str_to_decimal(s, base):
    total = 0
    for ch in s:
        if ch.isdigit():
            val = int(ch)
        else:
            val = ord(ch.upper()) - ord('A') + 10
        if val >= base:
            raise ValueError(f"Invalid character {ch} in base {base}")
        total = total * base + val
    return total

def decimal_to_roman(num):
    val = [
        (1000, 'M'), (900, 'CM'), (500, 'D'), (400, 'CD'),
        (100, 'C'), (90, 'XC'), (50, 'L'), (40, 'XL'),
        (10, 'X'), (9, 'IX'), (5, 'V'), (4, 'IV'),
        (1, 'I')
    ]
    res = []
    for (i, r) in val:
        while num >= i:
            res.append(r)
            num -= i
    return ''.join(res)

def decimal_to_base(n, base):
    if n == 0:
        return '0'
    digits = []
    while n > 0:
        rem = n % base
        if rem < 10:
            digits.append(str(rem))
        else:
            digits.append(chr(ord('A') + rem - 10))
        n = n // base
    return ''.join(reversed(digits))

