def min_ternary_string(s):
    # Convert the string into a list for easier manipulation
    s_list = list(s)

    # For each character, determine the target positions based on the allowed swaps
    # 0 can only be swapped with 1, so all 0s should come before 1s as much as possible
    # 2 can only be swapped with 1, so all 2s should come after 1s as much as possible

    # The relative order of 0s and 2s is fixed as per the original string
    # So, we can process the groups separately

    # Group the indices of each character
    zeros = []
    ones = []
    twos = []

    for i, c in enumerate(s_list):
        if c == '0':
            zeros.append(i)
        elif c == '1':
            ones.append(i)
        else:
            twos.append(i)

    # Now, process each group to move them to their correct positions
    # For 0s: move them as left as possible, but not crossing any 2s
    # For 2s: move them as right as possible, but not crossing any 0s
    # For 1s: they can be moved around both 0s and 2s

    # Process 0s first to move them as left as possible
    zero_ptr = 0
    for i in range(len(zeros)):
        pos = zeros[i]
        # Move 0 to the earliest possible position, which is the position where all previous 0s have been placed
        while zero_ptr < len(zeros) - 1:
            if zero_ptr == i:
                break
            if s_list[zero_ptr] == '0':
                zero_ptr += 1
            else:
                # Swap with the next character if it's a 0 or 1
                if zero_ptr + 1 >= len(s_list):
                    break
                if s_list[zero_ptr + 1] == '1':
                    # Swap them
                    s_list[zero_ptr], s_list[zero_ptr + 1] = s_list[zero_ptr + 1], s_list[zero_ptr]

    # Similarly, process 2s from the end
    two_ptr = len(s_list) - 1
    for i in reversed(range(len(twos))):
        pos = twos[i]
        while two_ptr > len(twos) - 1:
            if two_ptr == i:
                break
            if s_list[two_ptr] == '2':
                two_ptr -= 1
            else:
                if two_ptr - 1 < 0:
                    break
                if s_list[two_ptr - 1] == '1':
                    # Swap them
                    s_list[two_ptr - 1], s_list[two_ptr] = s_list[two_ptr], s_list[two_ptr - 1]

    # Now, handle 1s which can be moved freely among themselves
    # We can sort the 1s in their current positions
    one_indices = [i for i, c in enumerate(s_list) if c == '1']
    for i in range(len(one_indices)):
        pos = one_indices[i]
        while i < len(one_indices) - 1:
            if one_indices[i] == pos:
                break
            if s_list[pos] == '1':
                pos -= 1
            else:
                if pos + 1 >= len(s_list):
                    break
                if s_list[pos + 1] == '1':
                    s_list[pos], s_list[pos + 1] = s_list[pos + 1], s_list[pos]

    return ''.join(s_list)

