def min_ternary_string(s):
    s = list(s)
    # First pass: move all '0's to the left
    for i in range(len(s)):
        if s[i] == '0':
            while i > 0 and s[i-1] == '1':
                s[i], s[i-1] = s[i-1], s[i]
                i -= 1
    # Second pass: move all '1's to the left, considering '0's are already passed
    for i in range(len(s)):
        if s[i] == '1':
            while i > 0 and (s[i-1] == '1' or s[i-1] == '0'):
                # If previous is '0', it's already moved, so break
                if s[i-1] == '0':
