import sys
from collections import deque, defaultdict

def hopcroft_karp(graph, n):
    pair_U = [-1] * n
    pair_V = [-1] * n
    dist = [0] * n

    def bfs():
        queue = deque()
        for u in range(n):
            if pair_U[u] == -1:
                dist[u] = 0
                queue.append(u)
            else:
                dist[u] = float('inf')
        dist_null = float('inf')
        while queue:
            u = queue.popleft()
            if dist[u] < dist_null:
                for v in graph[u]:
                    if pair_V[v] == -1:
                        dist_null = dist[u] + 1
                    elif dist[pair_V[v]] == float('inf'):
                        dist[pair_V[v]] = dist[u] + 1
                        queue.append(pair_V[v])
        return dist_null != float('inf')

    def dfs(u):
        for v in graph[u]:
            if pair_V[v] == -1 or (dist[pair_V[v]] == dist[u] + 1 and dfs(pair_V[v])):
                pair_U[u] = v
                pair_V[v] = u
                return True
        dist[u] = float('inf')
        return False

    result = 0
    while bfs():
        for u in range(n):
            if pair_U[u] == -1:
                if dfs(u):
                    result += 1
    return result

