from collections import deque
from sys import stdin, stdout

def dfs(node, visited, graph):
    """Perform DFS from the given node."""
    stack = [(node, [node])]
    while stack:
        n, path = stack.pop()
        if not visited[n]:
            visited[n] = True
            for neighbor in graph[n]:
                if neighbor not in path:
                    stack.append((neighbor, path + [neighbor]))

def find_odd_cycle(graph):
    """Find an odd cycle in the graph using DFS."""
    visited = {i: False for i in range(len(graph))}
    for i in range(len(graph)):
        if not visited[i]:
            dfs(i, visited, graph)
    for i in range(len(graph)):
        if len(graph[i]) % 2 != 0:
            return True
    return False

def find_articulation_points(graph):
    """Find articulation points in the graph using DFS."""
    visited = {i: False for i in range(len(graph))}
    low = {i: float('inf') for i in range(len(graph))}
    disc = {i: float('inf') for i in range(len(graph))}
    ap = set()
    parent = {i: -1 for i in range(len(graph))}
    time = 0

    def dfs(node):
        nonlocal time
        visited[node] = True
        disc[node] = time
        low[node] = time
        time += 1
        children = 0
        for neighbor in graph[node]:
            if not visited[neighbor]:
                parent[neighbor] = node
                children += 1
                dfs(neighbor)
                low[node] = min(low[node], low[neighbor])
                if (parent[node] == -1 and children > 1) or \
                   (parent[node] != -1 and low[neighbor] >= disc[node]):
                    ap.add(node)
            elif neighbor != parent[node]:
                low[node] = min(low[node], disc[neighbor])

    for i in range(len(graph)):
        if not visited[i]:
            dfs(i)

    return ap

def find_biconnected_components(graph):
    """Find biconnected components in the graph."""
    ap = find_articulation_points(graph)
    components = []
    visited = {i: False for i in range(len(graph))}
    for node in ap:
        if not visited[node]:
            component = set()
            stack = [node]
            while stack:
                n = stack.pop()
                visited[n] = True
                component.add(n)
                for neighbor in graph[n]:
                    if neighbor not in visited and neighbor != parent[n]:
                        stack.append(neighbor)
            components.append(component)
    return components

def solve():
    """Solve the problem."""
    n, m = map(int, stdin.readline().split())
    graph = [[] for _ in range(n)]
    for _ in range(m):
        u, v = map(int, stdin.readline().split())
        graph[u - 1].append(v - 1)
        graph[v - 1].append(u - 1)

    # Find a connected component with an odd number of edges.
    found = False
    for i in range(n):
        if len(graph[i]) % 2 != 0:
            found = True
            break

    if found:
        t = 0
        w = 1
        print(f"{t} {w}")
        return

    # Find a biconnected component with an odd number of edges.
    biconnected_components = find_biconnected_components(graph)
    for component in biconnected_components:
        if len(component) % 2 != 0:
            t = 1
            w = 1
            print(f"{t} {w}")
            return

    # The graph is disconnected and does not contain any cycles.
    t = 2
    w = 0
    print(f"{t} {w}")

solve()

