            # So the number of subsets for S_i is 2^{current_max - 1}
            # Because S_i must include current_max, and can include any subset of {1..current_max-1}
            ways = ways * pow(2, current_max, MOD)
            ways %= MOD

        result = (result + ways) % MOD

